protected function validate() {
    	if ((utf8_strlen($this->request->post['contactnumber']) < 3)) {
      		$this->error['contactnumber'] = $this->language->get('error_contactnumber');
    	}

		if (!$this->error) {
	  		return true;
		} else {
	  		return false;
		}  	  
  	}

